import axios from "axios";
import { handleResponse, authHeader } from "./ServiceHelpers";

export const itemService = {
  getItems,
  create,
  getById,
  update,
  deleteItem,
};

//these are not done; placeholders to get mvp

const requestOptions = {
  headers: { ...authHeader(), "Content-Type": "application/json" },
};

function getItems(listId) {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  return axios
    .get(`/items/getItems`, { params: { listId: listId } }, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function getById(itemId) {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  return axios
    .get(`/items/${itemId}`, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function create(body) {
  return axios
    .post(`/items/create`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deleteItem(listId, id) {
  return axios
    .delete(
      `/items/deleteItem/${id}`,
      { params: { listId: listId } },
      requestOptions
    )
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function update(body) {
  return axios
    .put(`/items/${body._id}`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}
