import axios from "axios";
import { handleResponse, authHeader } from "./ServiceHelpers";

export const pageService = {
  getPages,
  getSheets,
  createPage,
  createSheet,
  getById,
  update,
  deletePage,
  deleteSheet,
  deleteList,
};

const requestOptions = {
  headers: { ...authHeader(), "Content-Type": "application/json" },
};

function getPages() {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  const username = localStorage.getItem("user");
  return axios
    .get(`/pages/getPages`, { params: { username: username } }, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function getSheets(pageId) {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  return axios
    .get(`/pages/getSheets`, { params: { pageId: pageId } }, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function getById(sheetId) {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  return axios
    .get(`/pages/`, { params: { sheetId: sheetId } }, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function createPage(body) {
  const username = localStorage.getItem("user");
  body["username"] = username;
  console.log(body);
  return axios
    .post(`/pages/createPage`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function createSheet(body) {
  return axios
    .post(`/pages/createSheet`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function update(body) {
  return axios
    .put(`/pages/`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deletePage(id) {
  const username = localStorage.getItem("user");
  return axios
    .delete(
      `/pages/deletePage/${id}`,
      { params: { username: username } },
      requestOptions
    )
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deleteSheet(pageId, id) {
  return axios
    .delete(
      `/pages/deleteSheet/${id}`,
      { params: { pageId: pageId } },
      requestOptions
    )
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deleteList(sheetId, id) {
  return axios
    .delete(
      `/pages/deleteList/${id}`,
      { params: { sheetId: sheetId } },
      requestOptions
    )
    .then(handleResponse)
    .catch((err) => console.log(err));
}
