import React, { Component } from "react";
// import axios from "axios";
import Button from "@material-ui/core/Button";

class Input extends Component {
  state = {
    action: "",
  };

  addTodo = () => {
    if (this.state.action.length > 0) {
      this.props.addTodo({ action: this.state.action });
    }
  };

