import { itemService } from "../services/ItemService";

//Actions
const ITEM_REQUEST = "components/common/ITEM_REQUEST";
const ITEM_SUCCESS = "components/common/ITEM_SUCCESS";
const ITEM_FAILURE = "components/common/ITEM_FAILURE";

//Reducer
export default function itemReducer(state = {}, action) {
  switch (action.type) {
    case ITEM_REQUEST:
      return Object.assign({}, state, {
        gettingItems: true,
      });
    case ITEM_SUCCESS:
      return Object.assign({}, state, {
        items: action.items,
      });
    case ITEM_FAILURE:
      return Object.assign({}, state, {
        error: true,
      });
    default:
      return state;
  }
}

//Action Creators
export function getItems(listId) {
  return (dispatch) => {
    dispatch(request());
    itemService.getItems(listId).then(
      (items) => {
        dispatch(success(items));
      },
      (error) => {
        dispatch(failure(error.toString()));
        // dispatch(alertActions.error(error.toString()));
      }
    );
  };

  function request() {
    return { type: ITEM_REQUEST };
  }
  function success(items) {
    return { type: ITEM_SUCCESS, items };
  }
  function failure(error) {
    return { type: ITEM_FAILURE, error };
  }
}

function _handleItem(apiCall, body) {
  return (dispatch) => {
    dispatch({ type: ITEM_REQUEST });
    apiCall(body).then(
      (items) => {
        // dispatch({ type: ITEM_SUCCESS, items });
        dispatch(getItems(body.sheetId));
      },
      (error) => {
        dispatch({ type: ITEM_FAILURE, payload: error.toString() });
      }
    );
  };
}

export function addItem(body) {
  return _handleItem(itemService.create, body);
}
export function deleteItem(body) {
  return _handleItem(itemService.deleteItem, body);
}
export function updateItem(body) {
  return _handleItem(itemService.update, body);
}
