import React from "react";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({
  className,
  pageId,
  sheet,
  lists,
  addList,
  deleteSheet,
  ...rest
}) => {
  return (
    <Grid
      className={className}
      container
      spacing={1}
      direction="row"
      justify="space-evenly"
      alignItems="flex-start"
      style={{ padding: "10px" }}
    >
      {lists && lists.length > 0 ? (
        lists.map((list) => {
          return (
            <Grid item key={list._id}>
              <DraggableList
                sheetId={sheet._id}
                listId={list._id}
                header={list.name}
                items={list.items}
                {...rest}
              />
            </Grid>
          );
        })
      ) : (
        <h1> Lists do not yet exist </h1>
      )}
      <AddDialog
        header="Add List"
        fields={{ "List Name": "name" }}
        addFunction={addList}
        body={{ pageId: sheet._id }}
      />
      <IconButton
        edge="end"
        aria-label="delete"
        onClick={() => deleteSheet({ pageId, sheetId: sheet._id })}
      >
        <CloseIcon />
      </IconButton>
    </Grid>
  );
};

export default Sheet;
