import { todoService } from "../../../services/TodoService";

//Actions
const GET_TODOS_REQUEST = "components/Todo/GET_TODOS_REQUEST";
const GET_TODOS_SUCESS = "components/Todo/GET_TODOS_SUCCESS";
const FAILURE = "components/Todo/GET_TODOS_FAILURE";
const ADD_TODOS_REQUEST = "components/Todo/ADD_TODOS_REQUEST";

//Reducer
export default function todoReducer(state = [], action) {
  switch (action.type) {
    case GET_TODOS_REQUEST:
      console.log(action);
      return action.todos;
    default:
      return state;
  }
}

//Action Creators
export function getTodos(todos) {
  console.log(todos);
  return (dispatch) => {
    dispatch(request({ todos }));
    todoService.getTodos().then(
      (todos) => {
        dispatch(success(todos));
      },
      (error) => {
        dispatch(failure(error.toString()));
      }
    );
  };
  function request(user) {
    return { type: LOGIN_REQUEST, user };
  }
  function success(todos) {
    return { type: GET_TODOS_SUCESS, todos };
  }
  function failure(error) {
    return { type: LOGIN_FAILURE, error };
  }
}

//Side effects as applicable
