// import config from "config";
import axios from "axios";

export function authHeader() {
  // return authorization header with jwt token
  let user = JSON.parse(localStorage.getItem("user"));

  if (user && user.token) {
    return { Authorization: "Bearer " + user.token };
  } else {
    return {};
  }
}

export const userService = {
  login,
  logout,
  register,
  getAll,
  getById,
  update,
  delete: _delete,
};

function login(username, password) {
  let config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  return axios
    .post("/users/authenticate", { username, password }, config)
    .then(handleResponse)

    .then((data) => {
      // console.log(response);

      const user = data.username;
      // store user details and jwt token in local storage to keep user logged in between page refreshes
      localStorage.setItem("user", user);
      localStorage.setItem("hash", data.passwordHash);
      console.log(user);
      return user;
    });
}
// .then((user) => {
//   console.log(user);
//   // store user details and jwt token in local storage to keep user logged in between page refreshes
//   localStorage.setItem("user", user);
//   return user;
// });

function logout() {
  // remove user from local storage to log user out
  localStorage.removeItem("user");
}

function getAll() {
  const requestOptions = {
    method: "GET",
    headers: authHeader(),
  };

  return fetch(`/users/`, requestOptions).then(handleResponse);
}

function getById(id) {
  const requestOptions = {
    method: "GET",
    headers: authHeader(),
  };

  return fetch(`/users/${id}`, requestOptions).then(handleResponse);
}

function register(user) {
  return axios.post(`/users/register`, user).then(handleResponse);
}

function update(user) {
  const requestOptions = {
    method: "PUT",
    headers: { ...authHeader(), "Content-Type": "application/json" },
    body: JSON.stringify(user),
  };

  return fetch(`/users/${user.id}`, requestOptions).then(handleResponse);
}

// prefixed function name with underscore because delete is a reserved word in javascript
function _delete(id) {
  const requestOptions = {
    method: "DELETE",
    headers: authHeader(),
  };

  return fetch(`/users/${id}`, requestOptions).then(handleResponse);
}

function handleResponse(response) {
  if (response.status !== 200) {
    if (response.status === 401) {
      // auto logout if 401 response returned from api
      // logout();
      console.log("BAD");
      // location.reload(true);
    }

    // const error = (response && data.message) || response.statusText;
    console.log("error");
    // return Promise.reject(error);
  }
  console.log(response);
  return response.data;
  // });
}
