//     if (listId !== event.target._id) {
//       //get id of new list
//       //THIS IS DEFINITELY WRONG--UPDATE WHEN WE GET TO DOING ITEMS
//       const oldList = { listId: listId, ...selectedItem };
//       const newList = { listId: event.target._id, ...selectedItem };
//       addItem(newList);
//       deleteItem(oldList);
//     }
//   };
//   const onDragLeave = (event) => {
//     event.currentTarget.style.background = "";
//   };
//   return isDraggable ? (
//     <Card
//       onDragOver={(event) => onDragOver(event)}
//       onDrop={(event) => onDrop(event, listId, addItem, deleteItem)}
//       onDragLeave={(event) => onDragLeave(event)}
//       style={{ height: "30vw", width: "30vw" }}
//     >
//       {rest}
//     </Card>
//   ) : (
//     <Card style={{ height: "30vw", width: "30vw" }}>{rest}</Card>
//   );
// };

const DraggableList = ({
  isDraggable,
  sheetId,
  listId,
  header,
  items,
  moveItem,
  addItem,
  editItem,
  deleteItem,
}) => {
  const onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };

  const onDrop = (event, listId, items, moveItem, sheetId) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("item");
    console.log(itemId);

    let index =
      bottomItem !== items.length && bottomItem !== 0
        ? items.findIndex((i) => i._id === bottomItem)
        : bottomItem;

    console.log(index);
    let body = { listId, index, itemId };
    moveItem(body, sheetId);
  };

  const onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };

  let bottomItem = 0;
  const onDragOverItem = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightblue";
  };

  const onDropItem = (event, itemIndex) => {
    console.log(itemIndex);
    event.preventDefault();
    event.currentTarget.style.background = "";
    if (itemIndex !== bottomItem) {
      bottomItem = itemIndex;
    }
  };

  const onDragLeaveItem = (event) => {
    event.currentTarget.style.background = "";
  };

  return (
    <Card
      onDragOver={(event) => onDragOver(event)}
      onDrop={(event) => onDrop(event, listId, items, moveItem, sheetId)}
      onDragLeave={(event) => onDragLeave(event)}
      style={{ height: "30vw", width: "30vw" }}
    >
      {/* // <ListCard
    //   isDraggable={isDraggable || false}
    //   listId={listId}
    //   addItem={addItem}
    //   deleteItem={deleteItem}
    // > */}
      <h4
        onDrop={(event) => onDropItem(event, 0)}
        style={{ margin: "10px 0px" }}
      >
        {header}
      </h4>
      <List>
        <CardContent>
          {items && items.length > 0 ? (
            items.map((item) => {
              return (
                <DraggableListItem
                  key={item._id}
                  isDraggable
                  // isDraggable={isDraggable || false}
                  item={item}
                  onDragOver={(event) => onDragOverItem(event)}
                  onDrop={(event) => onDropItem(event, item._id)}
                  onDragLeave={(event) => onDragLeaveItem(event)}
                >
                  <ListItemText primary={item.name} />
                  {/* <ListItemButtons
                    item={item}
                    editItem={editItem}
                    deleteItem={deleteItem}
                  /> */}
                </DraggableListItem>
              );
            })
          ) : (
            <li>No items </li>
          )}
        </CardContent>
      </List>
      <CardActions onDrop={(event) => onDropItem(event, items.length)}>
        <AddDialog
          header="Add Item"
          fields={{ "Item Name": "name" }}
          // specialFields={[
          //   (changeFunction) => {
          //     return (
          //       <TextField
          //         margin="dense"
          //         key="Description"
          //         id="details"
          //         label="Description"
          //         fullWidth
          //         multiline
          //         required
          //         type="text"
          //         onChange={changeFunction}
          //       />
          //     );
          //   },
          // ]}
          addFunction={addItem}
          body={{ listId: listId, sheetId: sheetId }}
        />
      </CardActions>
      {/* </ListCard> */}
    </Card>
  );
};

export { DraggableList };
