import React, { Component } from "react";
import { connect } from "react-redux";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import { withRouter } from "react-router-dom";
import { getSheets, addSheet } from "../../redux/Sheet";
import { addList } from "../../redux/List";
import { addItem, updateItem, deleteItem } from "../../redux/Item";
import Sheet from "./Sheet";
import { AddDialog } from "./Dialog";

class Page extends Component {
  state = {
    sheet: undefined,
  };

  componentDidMount() {
    this.props.getSheets(this.props.pageId);
    console.log(this.state.sheet);
  }

  changeSheet = (sheetId) => {
    const sheet = this.props.sheets.find((sheet) => {
      return sheet._id === sheetId;
    });
    this.setState({ sheet });
  };

  // handleChange = (e) => {
  //   this.setState({ [e.target.id]: e.target.value });
  // };

  render() {
    console.log(this.state.sheet);
    return (
      <div>
        {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
        {/* push bar down below menu bar */}
        <AppBar>
          <Toolbar>
            {this.props.sheets
              ? this.props.sheets.map((sheet) => (
                  <Button
                    variant="outlined"
                    key={sheet._id}
                    onClick={() => this.changeSheet(sheet._id)}
                  >
                    {sheet.name}
                  </Button>
                ))
              : null}

            <AddDialog
              header="Add Sheet"
              fields={{ "Sheet Name": "name" }}
              addFunction={this.props.addSheet}
              body={{ pageId: this.props.pageId }}
            />
          </Toolbar>
        </AppBar>
        {this.props.sheets ? (
          <Sheet
            sheet={this.state.sheet || this.props.sheets[0]}
            isDraggable={true}
            lists={this.props.lists}
            addList={this.props.addList}
            addItem={this.props.addItem}
            editItem={this.props.updateItem}
            deleteItem={this.props.deleteItem}
            // handleChange={this.handleChange}
          />
        ) : (
          <h1>Sheets do not yet exist</h1>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.sheetState);
  return {
    sheets: state.sheetState.sheets,
    lists: state.listState.lists,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  addList,
  addItem,
  updateItem,
  deleteItem,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Page));
