import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({ pageId, sheet, lists, addList, deleteSheet, ...rest }) => {
  const useStyles = makeStyles((theme) => ({
    root: { position: "relative", height: "80vh" },
    grid: { padding: "10px" },
    gridItem: {
      height: "30vw",
      width: "30vw",
      // transition: theme.transitions.create(["margin", "width"], {
      //   easing: theme.transitions.easing.sharp,
      //   duration: theme.transitions.duration.leavingScreen,
      // }),
    },
    // appBarShift: {
    //   width: `calc(100% - ${drawerWidth}px)`,
    //   marginLeft: drawerWidth,
    //   transition: theme.transitions.create(["margin", "width"], {
    //     easing: theme.transitions.easing.easeOut,
    //     duration: theme.transitions.duration.enteringScreen,
    //   }),
    // },
    addButton: {
      position: "absolute",
      bottom: 0,
      right: 0,
    },
    closeButton: {
      position: "absolute",
      top: 0,
      right: 0,
    },
    hide: {
      display: "none",
    },
  }));

  const classes = useStyles();

  return (
    <div className={classes.root}>
      <IconButton
        className={classes.closeButton}
        // edge="end"
        aria-label="delete"
        onClick={() => deleteSheet({ pageId, sheetId: sheet._id })}
      >
        <CloseIcon />
      </IconButton>
      <Grid
        container
        spacing={1}
        direction="row"
        justify="space-evenly"
        alignItems="center"
        className={classes.grid}
      >
        {lists && lists.length > 0 ? (
          lists.map((list) => {
            return (
              <Grid item key={list._id} className={classes.gridItem}>
                <DraggableList
                  sheetId={sheet._id}
                  listId={list._id}
                  header={list.name}
                  items={list.items}
                  {...rest}
                />
              </Grid>
            );
          })
        ) : (
          <h1> Lists do not yet exist </h1>
        )}
      </Grid>
      <div className={classes.addButton}>
        <AddDialog
          header="Add List"
          fields={{ "List Name": "name" }}
          addFunction={addList}
          body={{ pageId: sheet._id }}
        />
      </div>
    </div>
  );
};

export default Sheet;
