import axios from "axios";
import { handleResponse } from "./ServiceHelpers";

export const todoService = {
  getTodos,
  addTodo,
  deleteTodo,
  updateTodo,
};

// const requestOptions = {
//   headers: { ...authHeader(), "Content-Type": "application/json" },
// };

function getTodos() {
  return axios
    .get("/todos/getTodos")
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function addTodo(todo) {
  return axios
    .post("/todos/createTodo", todo)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deleteTodo(id) {
  return axios
    .delete(`/todos/deleteTodo/${id}`)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function updateTodo(body) {
  return axios
    .put(`/todos/updateTodo/${body._id}`, body)
    .then(handleResponse)
    .catch((err) => console.log(err));
}
