import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import Input from "./Input";
import ListTodo from "./ListTodo";
import { getTodos } from "./ducks/Todo";
import Grid from "@material-ui/core/Grid";

const progress = ["Pending", "In progress", "Completed"];
const authHeader = () => {
  // return authorization header with jwt token
  let user = JSON.parse(localStorage.getItem("user"));
  let hash = JSON.parse(localStorage.getItem("hash"));

  if (user && hash) {
    return { Authorization: "Bearer " + hash };
  } else {
    return {};
  }
};
console.log(authHeader);
class Todo extends Component {
  componentDidMount() {
    this.getTodosFromDB();
  }

  getTodosFromDB = () => {
    const requestOptions = {
      headers: { ...authHeader, "Content-Type": "application/json" },
    };
    axios
      .get("/todos/getTodos", requestOptions)
      .then((res) => {
        if (res.data) {
          console.log(res.data);
          this.props.getTodos(res.data);
        }
      })
      .catch((err) => console.log(err));
  };

  deleteTodo = (id) => {
    axios
      .delete(`/todos/deleteTodo/${id}`)
      .then((res) => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch((err) => console.log(err));
  };

  updateProgress = (todo) => {
    let progressIndex = progress.indexOf(todo.progress);
    if (progressIndex !== 2) {
      let updatedTodo = {
        ...todo,
        progress: progress[progressIndex + 1],
      };
      this.updateTodo(updatedTodo);
    }
  };

  updateTodo = (body) => {
    axios
      .put(`/todos/updateTodo/${body._id}`, body)
      .then((res) => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch((err) => console.log(err));
  };

  onMouseOver = (event) => {
    event.currentTarget.style.cursor = "grab";
  };
  onDragStart = (event, todo) => {
    event.currentTarget.style.border = "dashed";
    event.dataTransfer.setData("todo", todo);
    // event.currentTarget.style.cursor = "grabbing";
  };
  onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };

  onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };
  onDrop = (event, progressState) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let todoId = event.dataTransfer.getData("todo");
    let todoItem = this.props.todos.find((i) => i._id === todoId);
    let progressIndex = progress.indexOf(todoItem.progress);
    let newProgressIndex = progress.indexOf(progressState);
    if (progressIndex !== newProgressIndex) {
      let updatedTodo = {
        ...todoItem,
        progress: progress[newProgressIndex],
      };
      this.updateTodo(updatedTodo);
    }
  };
  onDragEnd = (event) => {
    event.currentTarget.style.border = "";
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
  };

  render() {
    let { todos } = this.props;
    console.log(todos);
    return (
      <div>
        <div style={{ padding: "10px" }}>
          <h3>Todo List</h3>
          <Input getTodos={this.getTodosFromDB} />
        </div>
        <Grid
          container
          spacing={1}
          direction="row"
          justify="space-evenly"
          alignItems="flex-start"
          style={{ padding: "10px" }}
        >
          {progress.map((header) => {
            return (
              <Grid item key={progress.indexOf(header)}>
                <ListTodo
                  header={header}
                  todos={todos}
                  deleteTodo={this.deleteTodo}
                  updateProgress={this.updateProgress}
                  onMouseOver={this.onMouseOver}
                  onDrop={this.onDrop}
                  onDragOver={this.onDragOver}
                  onDragStart={this.onDragStart}
                  onDragEnd={this.onDragEnd}
                  onDragLeave={this.onDragLeave}
                />
              </Grid>
            );
          })}
        </Grid>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.todoState);
  return {
    todos: state.todoState,
  };
};

const mapDispatchToProps = {
  getTodos,
};

export default connect(mapStateToProps, mapDispatchToProps)(Todo);
