import axios from "axios";
import { authHeader, handleResponse } from "./ServiceHelpers";

export const todoService = {
  getTodos,
  addTodo,
  deleteTodo,
  updateTodo,
};
function getTodos() {
  const requestOptions = {
    headers: { ...authHeader(), "Content-Type": "application/json" },
  };
  return axios
    .get("/todos/getTodos", requestOptions)
    .then(handleResponse)
    .then((res) => (res.data ? res.data : {}))
    .catch((err) => console.log(err));
}

function addTodo(todo) {
  return (
    axios
      .post("/todos/createTodo", todo)
      .then(handleResponse)
      //   .then((res) => {
      //     if (res.data) {
      //       this.props.getTodos();
      //   this.setState({ action: "" });
      //     }
      //   })
      .catch((err) => console.log(err))
  );
}

function deleteTodo(id) {
  return (
    axios
      .delete(`/todos/deleteTodo/${id}`)
      .then(handleResponse)

      //   .then((res) => {
      //     if (res.data) {
      //       this.getTodosFromDB();
      //     }
      //   })
      .catch((err) => console.log(err))
  );
}

function updateTodo(body) {
  return (
    axios
      .put(`/todos/updateTodo/${body._id}`, body)
      .then(handleResponse)

      //   .then((res) => {
      //     if (res.data) {
      //       this.getTodosFromDB();
      //     }
      //   })
      .catch((err) => console.log(err))
  );
}
