import { todoService } from "../../../services/TodoService";

//Actions
const GET_TODOS_REQUEST = "components/Todo/GET_TODOS_REQUEST";
const GET_TODOS_SUCCESS = "components/Todo/GET_TODOS_SUCCESS";
const FAILURE = "components/Todo/GET_TODOS_FAILURE";
const TODO_REQUEST = "components/Todo/TODO_REQUEST";
const TODO_SUCCESS = "components/Todo/TODO_SUCCESS";

//Reducer
export default function todoReducer(state = [], action) {
  switch (action.type) {
    case GET_TODOS_REQUEST:
      return Object.assign({}, state, {
        gettingTodos: true,
      });
    case GET_TODOS_SUCCESS:
      return Object.assign({}, state, {
        todos: action.todos,
      });
    // return action.todos;
    case FAILURE:
      return Object.assign({}, state, {
        error: true,
      });
    default:
      return state;
  }
}

//Action Creators
export function getTodos() {
  return (dispatch) => {
    dispatch({ type: GET_TODOS_REQUEST });
    todoService.getTodos().then(
      (todos) => {
        dispatch({ type: GET_TODOS_SUCCESS, todos });
      },
      (error) => {
        dispatch({ type: FAILURE, payload: error.toString() });
      }
    );
  };
  function _request(type, action) {
    return { type: GET_TODOS_REQUEST, todos };
  }
  function _success(type, action) {
    return { type: GET_TODOS_SUCCESS, todos };
  }
  function _failure(type, action) {
    return { type: FAILURE, error };
  }
}

function _handleTodo(apiCall, body) {
  return (dispatch) => {
    dispatch({ type: TODO_REQUEST });
    apiCall(body).then(
      (todos) => {
        dispatch({ type: TODO_SUCCESS, todos });
        dispatch(getTodos());
      },
      (error) => {
        dispatch({ type: FAILURE, payload: error.toString() });
      }
    );
  };
}

export function addTodo(todo) {
  _handleTodo(todoService.addTodo, todo);
}
export function deleteTodo(id) {
  _handleTodo(todoService.deleteTodo, id);
}
export function updateTodo(todo) {
  _handleTodo(todoService.updateTodo, todo);
}

//Side effects as applicable
