import React, { useState } from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import HomeIcon from "@material-ui/icons/Home";
import MenuIcon from "@material-ui/icons/Menu";
import Drawer from "@material-ui/core/Drawer";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "../common/Dialog";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";

import clsx from "clsx";

export const LoggedInNav = ({
  pages,
  handleLogout,
  handleRedirect,
  handleHome,
  addPage,
  deletePage,
  username,
}) => {
  const drawerWidth = 240;
  const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
    },
    appBar: {
      transition: theme.transitions.create(["margin", "width"], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
      transition: theme.transitions.create(["margin", "width"], {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    hide: {
      display: "none",
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    drawerHeader: {
      display: "flex",
      alignItems: "center",
      padding: theme.spacing(0, 1),
      // necessary for content to be below app bar
      ...theme.mixins.toolbar,
      justifyContent: "flex-end",
    },
    content: {
      flexGrow: 1,
      padding: theme.spacing(3),
      transition: theme.transitions.create("margin", {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      marginLeft: -drawerWidth,
    },
    contentShift: {
      transition: theme.transitions.create("margin", {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    },
  }));
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  const classes = useStyles();
  const theme = useTheme();
  return (
    <div className={classes.root}>
      <CssBaseline />

      <AppBar
        position="fixed"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: anchorEl,
        })}
      >
        <Toolbar variant="dense">
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={handleClick}
            className={clsx(classes.menuButton, anchorEl && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          {/* <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            {pages &&
              pages.map((page) => {
                return (
                  <MenuItem
                    key={page._id}
                    onClick={() => handleRedirect(page.name)}
                  >
                    {page.name}
                    <IconButton
                      edge="end"
                      aria-label="delete"
                      onClick={() => deletePage({ username, pageId: page._id })}
                    >
                      <CloseIcon />
                    </IconButton>
                  </MenuItem>
                );
              })}
            <MenuItem>
              <AddDialog
                header="Add Page"
                fields={{ "Page Name": "name" }}
                addFunction={addPage}
                // body={state}
              />
            </MenuItem>
          </Menu> */}
          <IconButton onClick={handleHome}>
            <HomeIcon color="secondary" />
          </IconButton>
          <Button
            style={{ position: "absolute", right: "50px" }}
            variant="contained"
            onClick={handleLogout}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={Boolean(anchorEl)}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>
          {pages &&
            pages.map((page) => {
              return (
                <ListItem
                  button
                  key={page._id}
                  onClick={() => handleRedirect(page.name)}
                >
                  <ListItemText primary={page.name} />
                  <IconButton
                    edge="end"
                    aria-label="delete"
                    onClick={() => deletePage({ username, pageId: page._id })}
                  >
                    <CloseIcon />
                  </IconButton>
                </ListItem>
              );
            })}
        </List>
        <AddDialog
          header="Add Page"
          fields={{ "Page Name": "name" }}
          addFunction={addPage}
        />
      </Drawer>
    </div>
  );
};

export const LoggedOutNav = ({ handleLogin }) => {
  return (
    <AppBar position="fixed">
      <Toolbar variant="dense">
        <Button variant="contained" onClick={handleLogin}>
          Admin Login
        </Button>
        <Button color="inherit">Login</Button>
      </Toolbar>
    </AppBar>
  );
};
