import React from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import AppBar from "@material-ui/core/AppBar";
import { LoggedInNav, LoggedOutNav } from "./NavViews";
import { login, logout } from "../../redux/Login";
import { addPage, deletePage } from "../../redux/Page";

class NavBar extends React.Component {
  handleLogin = (e) => {
    e.preventDefault();
    this.props.login("tester", "pass", this.props.history);
  };
  handleLogout = () => {
    this.props.logout(this.props.history);
  };
  handleRedirect = (e) => {
    this.props.history.push(`/${e.target.textContent}`);
  };
  handleHome = () => {
    this.props.history.push(`/`);
  };

  // addPage = () => {
  //   console.log("add placeholder");
  // };

  render() {
    console.log(this.props.pages);
    return (
      <AppBar position="static">
        {this.props.loggedIn && this.props.pages ? (
          <LoggedInNav
            pages={this.props.pages}
            // menu={this.props.menu}
            handleLogout={this.handleLogout}
            handleRedirect={this.handleRedirect}
            handleHome={this.handleHome}
            addPage={this.props.addPage}
            deletePage={this.props.deletePage}
            username={this.props.user}
          />
        ) : (
          <LoggedOutNav
            handleLogin={this.handleLogin}
            handleRedirect={this.handleRedirect}
          />
        )}
      </AppBar>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.pageState);
  return {
    pages: state.pageState.pages,
    loggingIn: state.loginState.loggingIn,
    loggedIn: state.loginState.loggedIn,
    logout: state.loginState.logout,
    user: state.loginState.user,
    error: state.pageState.error,
    // menu: state.pageState.menu,
  };
};

const mapDispatchToProps = {
  login,
  logout,
  addPage,
  deletePage,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(NavBar));
