import React, { Component } from "react";
import { connect } from "react-redux";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import AccountCircle from "@material-ui/icons/AccountCircle";
import HttpsIcon from "@material-ui/icons/Https";
import Form from "../common/Form";
import * as actions from "./ducks/Login";

const loginContent = (handleChange) => [
  {
    id: "0",
    icon: <AccountCircle />,
    text: (
      <TextField
        id="loginUsername"
        label="Username"
        onChange={handleChange}
        error={false}
      />
    ),
  },
  {
    id: "1",
    icon: <HttpsIcon />,
    text: (
      <TextField
        id="loginPassword"
        label="Password"
        type="password"
        onChange={handleChange}
      />
    ),
  },
];

const loginButton = (
  <Button variant="contained" type="submit">
    Login
  </Button>
);

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loginUsername: "",
      loginPassword: "",
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };

  handleLogin = () => {
    //check inputs
    console.log("hit");
    if (this.state.loginUsername && this.state.loginPassword) {
      console.log(this.state.loginUsername + this.state.loginPassword);
      // this.props.login(this.state.loginUsername, this.state.loginPassword);
    }
    //return logged in state (which should trigger hoc redirect if logged in)
  };

  render() {
    return (
      <Form
        title="Login"
        formContent={loginContent(this.handleChange)}
        submit={this.handleLogin}
        button={loginButton}
      />
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.loginState);
  return {
    loggingIn: state.loginState.loggingIn,
  };
};

const mapDispatchToProps = {
  login: actions.login,
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
