import React from "react";
import Grid from "@material-ui/core/Grid";
import CloseIcon from "@material-ui/icons/Close";
import IconButton from "@material-ui/core/IconButton";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({ sheet, lists, addList, deleteList, ...rest }) => {
  return (
    <Grid
      container
      spacing={1}
      direction="row"
      justify="space-evenly"
      alignItems="flex-start"
      style={{ padding: "10px" }}
    >
      {lists && lists.length > 0 ? (
        lists.map((list) => {
          return (
            <Grid item key={list._id}>
              <Grid item horizontal>
                <h5>{list._id}</h5>
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={() =>
                    deleteList({ listId: list._id, pageId: sheet._id })
                  }
                >
                  <CloseIcon />
                </IconButton>{" "}
              </Grid>
              <DraggableList
                sheetId={sheet._id}
                listId={list._id}
                header={list.name}
                items={list.items}
                {...rest}
              />
            </Grid>
          );
        })
      ) : (
        <h1> Lists do not yet exist </h1>
      )}
      <AddDialog
        header="Add List"
        fields={{ "List Name": "name" }}
        addFunction={addList}
        body={{ pageId: sheet._id }}
      />
    </Grid>
  );
};

export default Sheet;
