import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import HomeIcon from "@material-ui/icons/Home";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import Drawer from "@material-ui/core/Drawer";
import CloseIcon from "@material-ui/icons/Close";
import EditIcon from "@material-ui/icons/Edit";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import Input from "@material-ui/core/Input";
import { AddDialog } from "../common/Dialog";

export const LoggedInNav = ({
  pages,
  handleLogout,
  handleRedirect,
  handleHome,
  addPage,
  deletePage,
  username,
  toggleMenu,
  open,
}) => {
  const drawerWidth = 240;

  const useStyles = makeStyles((theme) => ({
    appBar: {
      transition: theme.transitions.create(["margin", "width"], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
    },
    appBarShift: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
      transition: theme.transitions.create(["margin", "width"], {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    hide: {
      display: "none",
    },
    drawer: {
      width: drawerWidth,
      flexShrink: 0,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    drawerHeader: {
      display: "flex",
      alignItems: "center",
      padding: theme.spacing(0, 1),
      // necessary for content to be below app bar
      ...theme.mixins.toolbar,
      justifyContent: "flex-end",
    },
  }));
  const [editClicked, setEditClicked] = useState(false);
  const classes = useStyles();
  return (
    <div>
      <AppBar
        position="fixed"
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <Toolbar variant="dense">
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={toggleMenu}
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <IconButton onClick={handleHome}>
            <HomeIcon color="secondary" />
          </IconButton>
          <Button
            style={{ position: "absolute", right: "50px" }}
            variant="contained"
            onClick={handleLogout}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.drawerHeader}>
          <IconButton onClick={toggleMenu}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>
          {pages.length > 0 &&
            pages.map((page) => {
              return editClicked ? (
                <ListItemOnEdit
                  page={page}
                  editPage={editPage}
                  deletePage={deletePage}
                />
              ) : (
                <ListItemOnView
                  page={page}
                  handleRedirect={handleRedirect}
                  setEditClicked={setEditClicked}
                />
              );
            })}
        </List>
        <AddDialog
          header="Add Page"
          fields={{ "Page Name": "name" }}
          addFunction={addPage}
        />
      </Drawer>
    </div>
  );
};

const ListItemOnView = ({ page, handleRedirect, setEditClicked }) => {
  return (
    <ListItem button key={page._id} onClick={() => handleRedirect(page.name)}>
      <ListItemText primary={page.name} />

      <IconButton
        edge="end"
        aria-label="edit"
        onClick={() => setEditClicked(true)}
      >
        <EditIcon />
      </IconButton>
    </ListItem>
  );
};
const ListItemOnEdit = ({ page, editPage, deletePage }) => {
  const [inputText, setInputText] = useState(page.name);
  // const enter = useKeypress('Enter');
  useEffect(() => {
    // if Enter is pressed, save the text and case the editor
    // if (enter) {
    //   props.onSetText(inputValue);
    //   setIsInputActive(false);
    // }
  }, [enter]); // watch the Enter key presses

  return (
    <ListItem button key={page._id} onClick={() => editPage(page.name)}>
      <Input value={inputText} onChange={(e) => setInputText(e.target.value)} />
      <IconButton
        edge="end"
        aria-label="delete"
        onClick={() => deletePage({ username, pageId: page._id })}
      >
        <CloseIcon />
      </IconButton>
    </ListItem>
  );
};

export const LoggedOutNav = ({ handleLogin }) => {
  return (
    <AppBar position="fixed">
      <Toolbar variant="dense">
        <Button variant="contained" onClick={handleLogin}>
          Admin Login
        </Button>
        <Button color="inherit">Login</Button>
      </Toolbar>
    </AppBar>
  );
};

LoggedInNav.propTypes = {
  username: PropTypes.string,
  pages: PropTypes.arrayOf(
    PropTypes.shape({
      _id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      sheets: PropTypes.array.isRequired,
      items: PropTypes.array.isRequired,
    })
  ).isRequired,
  open: PropTypes.bool.isRequired,

  handleRedirect: PropTypes.func.isRequired,
  handleHome: PropTypes.func.isRequired,
  addPage: PropTypes.func.isRequired,
  deletePage: PropTypes.func.isRequired,
  toggleMenu: PropTypes.func.isRequired,
};

ListItemOnView.propTypes = {
  page: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    sheets: PropTypes.array.isRequired,
    items: PropTypes.array.isRequired,
  }),
  handleRedirect: PropTypes.func.isRequired,
  setEditClicked: PropTypes.func.isRequired,
};

LoggedOutNav.propTypes = {
  handleLogin: PropTypes.func.isRequired,
};
