import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import CircularProgress from "@material-ui/core/CircularProgress";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({
  pageId,
  sheet,
  lists,
  addList,
  deleteSheet,
  loading,
  ...rest
}) => {
  const useStyles = makeStyles((theme) => ({
    root: {
      position: "relative",
      height: "85vh",
      padding: theme.spacing(5, 0),
      backgroundColor: "whitesmoke",
    },
    grid: { padding: theme.spacing(1, 3) },
    addButton: {
      position: "absolute",
      bottom: "10vh",
      right: "5vh",
    },
    closeButton: {
      position: "absolute",
      top: 0,
      right: 0,
    },
    hide: {
      display: "none",
    },
  }));

  const classes = useStyles();

  return (
    <div className={classes.root}>
      <IconButton
        className={classes.closeButton}
        aria-label="delete"
        onClick={() => deleteSheet({ pageId, sheetId: sheet._id })}
      >
        <CloseIcon fontSize="large" />
      </IconButton>
      <Grid
        container
        spacing={1}
        direction="row"
        justify="space-evenly"
        alignItems="center"
        className={classes.grid}
      >
        {/* {lists && lists.length > 0 ? (
          lists.map((list) => {
            return (
              <Grid item key={list._id} className={classes.gridItem}>
                <DraggableList
                  sheetId={sheet._id}
                  listId={list._id}
                  header={list.name}
                  items={list.items}
                  {...rest}
                />
              </Grid>
            );
          })
        ) : ( */}
        <div style={{ marginTop: "30vh" }}>
          {/* {loading ? ( */}
          <CircularProgress />
          {/* ) : (
            <Typography align="center" variant="h3">
              Lists do not yet exist
            </Typography>
          )} */}
        </div>
        {/* )} */}
      </Grid>
      <div className={classes.addButton}>
        <AddDialog
          header="Add List"
          fields={{ "List Name": "name" }}
          addFunction={addList}
          body={{ pageId: sheet._id }}
        />
      </div>
    </div>
  );
};

export default Sheet;
