import React from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import IconButton from "@material-ui/core/IconButton";
import NavigateNextSharpIcon from "@material-ui/icons/NavigateNextSharp";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import Divider from "@material-ui/core/Divider";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import Button from "@material-ui/core/Button";

const DraggableListItem = ({ item, deleteItem, ...rest }) => {
  const onMouseOver = (event) => {
    event.currentTarget.style.cursor = "grab";
  };
  const onDragStart = (event, item) => {
    event.currentTarget.style.border = "dashed";
    event.dataTransfer.setData("item", item);
    event.dataTransfer.effectAllowed = "move";
    // event.currentTarget.style.cursor = "grabbing";
  };

  const onDragEnd = (event) => {
    event.currentTarget.style.border = "";
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
  };

  return (
    <ListItem
      draggable
      onMouseOver={(event) => onMouseOver(event)}
      onDragStart={(event) => onDragStart(event, item._id)}
      onDragEnd={(event) => onDragEnd(event)}
      {...rest}
    >
      {" "}
      <ListItemText primary={item.name} />
      <Divider orientation="vertical" flexItem />
      <IconButton
        edge="end"
        aria-label="delete"
        onClick={() => deleteItem({ listId, itemId: item._id, sheetId })}
      >
        <DeleteIcon />
      </IconButton>
    </ListItem>
  );
};

const ListItemButtons = ({ item, editItem, deleteItem }) => {
  const actionTypes = { edit, del };
  // const add = (
  //   <IconButton edge="end" aria-label="add" onClick={() => addItem(item)}>
  //     <NavigateNextSharpIcon />
  //   </IconButton>
  // );
  // const update = (
  //   <IconButton
  //     edge="end"
  //     aria-label="update"
  //     onClick={() => updateItemProgress(item)}
  //   >
  //     <NavigateNextSharpIcon />
  //   </IconButton>
  // );
  const edit = (
    <IconButton edge="end" aria-label="edit" onClick={() => editItem(item)}>
      <NavigateNextSharpIcon />
    </IconButton>
  );
  const del = (
    <IconButton
      edge="end"
      aria-label="delete"
      onClick={() => deleteItem(item._id)}
    >
      <DeleteIcon />
    </IconButton>
  );

  const ActionMenu = () => {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
      setAnchorEl(null);
    };
    return (
      <div>
        <Button
          aria-controls="simple-menu"
          aria-haspopup="true"
          onClick={handleClick}
        >
          <MoreVertIcon />
        </Button>
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          keepMounted
          open={open}
          onClose={handleClose}
        >
          {actionTypes.map((action) => {
            return <MenuItem onClick={handleClose}>{action}</MenuItem> || null;
          })}
        </Menu>
      </div>
    );
  };

  // const ActionInline = (
  //   <ListItemSecondaryAction>
  //     {actions.map((action) => {
  //       return actionTypes[action] || null;
  //     })}
  //   </ListItemSecondaryAction>
  // );

  // const ActionDisplay = actions.length > 2 ? <ActionMenu /> : <ActionInline />;

  return (
    <div>
      <Divider orientation="vertical" flexItem />
      <ActionMenu />
    </div>
  );
};

export { DraggableListItem, ListItemButtons };
