//Actions
const TOGGLE_MENU = "components/OPEN_MENU";
const CLEAR_UI = "components/CLEAR_UI";

//Reducer
export default function uiReducer(state = { menuOpen: false }, action) {
  switch (action.type) {
    case TOGGLE_MENU:
      return Object.assign({}, state, {
        menuOpen: !state.menuOpen,
      });
    case CLEAR_UI:
      return Object.assign({}, state, {
        menuOpen: false,
      });
    default:
      return state;
  }
}

//Action Creators
export function toggleMenu() {
  return (dispatch) => {
    dispatch({ type: TOGGLE_MENU });
  };
}
