import React, { Component } from "react";
import clsx from "clsx";
import { connect } from "react-redux";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import { withRouter } from "react-router-dom";
import { getSheets, addSheet, deleteSheet } from "../../redux/Sheet";
import { getLists, addList, deleteList } from "../../redux/List";
import { addItem, updateItem, deleteItem, moveItem } from "../../redux/Item";
import Sheet from "./Sheet";
import { AddDialog } from "./Dialog";
import { Home } from "../home";
import { withStyles } from "@material-ui/core/styles";

const drawerWidth = 240;
const styles = (theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: "flex-end",
    marginTop: "10vh",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  loggedOutContent: {
    flexGrow: 1,
    padding: theme.spacing(3),
    marginLeft: 0,
  },
});

class Page extends Component {
  state = {
    sheet: undefined,
  };

  componentDidMount() {
    if (this.props.location.pathname !== "/") {
      this.props.getSheets(this.props.pageId);
    }
  }

  changeSheet = (sheetId) => {
    if (
      (this.state.sheet === undefined &&
        sheetId !== this.props.sheets[0]._id) ||
      (this.state.sheet && sheetId !== this.state.sheet._id)
    ) {
      const sheet = this.props.sheets.find((sheet) => {
        return sheet._id === sheetId;
      });
      this.setState({ sheet });
      this.props.getLists(sheetId);
    }
  };

  render() {
    const { classes } = this.props;
    return (
      <main
        className={clsx({
          [classes.loggedOutContent]: !this.props.loggedIn,
          [classes.content]: this.props.loggedIn,
          [classes.contentShift]: this.props.open,
        })}
      >
        <div className={classes.drawerHeader} />
        {this.props.location.pathname === "/" ? (
          <Home />
        ) : (
          // {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
          <>
            <AppBar
              color="transparent"
              // style={{ marginTop: "10vh" }}
              className={clsx(classes.appBar, {
                [classes.appBarShift]: this.props.open,
              })}
            >
              <Toolbar>
                {this.props.sheets
                  ? this.props.sheets.map((sheet) => (
                      <Button
                        variant="outlined"
                        key={sheet._id}
                        onClick={() => this.changeSheet(sheet._id)}
                      >
                        {sheet.name}
                      </Button>
                    ))
                  : null}

                <AddDialog
                  header="Add Sheet"
                  fields={{ "Sheet Name": "name" }}
                  addFunction={this.props.addSheet}
                  body={{ pageId: this.props.pageId }}
                />
              </Toolbar>
            </AppBar>
            <div className={classes.drawerHeader} />

            {this.props.sheets && this.props.sheets.length > 0 ? (
              <Sheet
                pageId={this.props.pageId}
                sheet={this.state.sheet || this.props.sheets[0]}
                isDraggable={true}
                deleteSheet={this.props.deleteSheet}
                lists={this.props.lists}
                addList={this.props.addList}
                deleteList={this.props.deleteList}
                moveItem={this.props.moveItem}
                addItem={this.props.addItem}
                editItem={this.props.updateItem}
                deleteItem={this.props.deleteItem}
              />
            ) : (
              <h1>Sheets do not yet exist</h1>
            )}
          </>
        )}
      </main>
    );
  }
}

const mapStateToProps = (state) => {
  // console.log(state.sheetState);
  // console.log(state.listState);
  return {
    loggedIn: state.loginState.loggedIn,
    sheets: state.sheetState.sheets,
    lists: state.listState.lists,
    open: state.uiState.menuOpen,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  deleteSheet,
  getLists,
  addList,
  deleteList,
  moveItem,
  addItem,
  updateItem,
  deleteItem,
};

const styledPage = withStyles(styles, { withTheme: true })(Page);
export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(styledPage)
);
