import { pageService } from "../services/PageService";
import { getLists } from "./List";
//Actions
const SHEET_REQUEST = "components/common/SHEET_REQUEST";
const SHEET_SUCCESS = "components/common/SHEET_SUCCESS";
const SHEET_FAILURE = "components/common/SHEET_FAILURE";

//Reducer
export default function sheetReducer(state = {}, action) {
  switch (action.type) {
    case SHEET_REQUEST:
      return Object.assign({}, state, {
        gettingSheets: true,
      });
    case SHEET_SUCCESS:
      return Object.assign({}, state, {
        sheets: action.sheets,
      });
    case SHEET_FAILURE:
      return Object.assign({}, state, {
        error: true,
      });
    default:
      return state;
  }
}

//Action Creators
export function getSheets(pageId) {
  return (dispatch) => {
    dispatch(request());
    pageService.getSheets(pageId).then(
      (sheets) => {
        dispatch(success(sheets));
        if (sheets) {
          console.log(sheets[0]._id);
          dispatch(getLists(sheets[0]._id));
        }
      },
      (error) => {
        dispatch(failure(error.toString()));
        // dispatch(alertActions.error(error.toString()));
      }
    );
  };

  function request() {
    return { type: SHEET_REQUEST };
  }
  function success(sheets) {
    return { type: SHEET_SUCCESS, sheets };
  }
  function failure(error) {
    return { type: SHEET_FAILURE, error };
  }
}

function _handleAction(apiCall, body) {
  return (dispatch) => {
    dispatch({ type: SHEET_REQUEST });
    apiCall(body).then(
      (sheets) => {
        console.log(sheets);
        // dispatch({ type: PAGE_SUCCESS, pages });
        dispatch(getSheets(body.pageId));
      },
      (error) => {
        dispatch({ type: SHEET_FAILURE, error });
      }
    );
  };
}

export function addSheet(body) {
  return _handleAction(pageService.createSheet, body);
}
export function deleteSheet(body) {
  return _handleAction(pageService.deleteSheet, body);
}
export function updateSheet(body) {
  return _handleAction(pageService.update, body);
}
