import React, {useState} from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import HomeOutlinedIcon from "@material-ui/icons/HomeOutlined";
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Register from "./Register";
import Login from "./Login";
import HomeIcon from "./HomeIcon.jpg"

const Home = ({ loggedIn }) => {

  const useStyles = makeStyles((theme) => ({
    root: {
      backgroundImage: `url(${HomeIcon})`,
      // position: 'relative',
      height: '100vh',
      width: '100vw',
      // display: 'flex',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: '0px',
        right: '0px',
        bottom: '0px',
        left: '0px',
        backgroundColor: 'rgba(0,0,0,0.25)'
      }
    },
    paper: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    hide: {
      display: "none",
    },
    content:{
      // opacity: '80%'
    }

  }));
  const classes = useStyles();
  const [value, setValue] = useState(0);

  return (<div>
    <div className={classes.root}/>
    <Card className={classes.content}>
      <CardHeader title={"LIST APP"}/>
      <CardContent >
        <AppBar position="static">
          <Tabs value={value} onChange={(e, newValue)=>setValue(newValue)} aria-label="tabs" centered>
            <Tab label="Login" id="simple-tab-0" aria-controls="simple-tabpanel-0"/>
            <Tab label="Register" id="simple-tab-1" aria-controls="simple-tabpanel-1"/>
          </Tabs>
        </AppBar>
        <div
          hidden={value !== 0}
          id={`simple-tabpanel-0`}
          aria-labelledby={`simple-tab-0`}>
          <Login/>
        </div>
        <div
          hidden={value !== 1}
          id={`simple-tabpanel-1`}
          aria-labelledby={`simple-tab-1`}>
          <Register />
        </div>
      </CardContent>
    </Card>
    </div>
    // <Grid container component="main" className={classes.root}>
    //   <Grid item xs={false} sm={4} md={7}  >
    //     <HomeOutlinedIcon style={{ fontSize: "30vh" }} color={"action"} />
    //   </Grid>
    //   <Grid item xs={12} sm={8} md={5} elevation={6}  
    //     className={clsx(classes.paper, loggedIn && classes.hide)}                  >
    //     <div >
    //       <AppBar position="static">
    //         <Tabs value={value} onChange={(e, newValue)=>setValue(newValue)} aria-label="tabs" centered>
    //           <Tab label="Login" id="simple-tab-0" aria-controls="simple-tabpanel-0"/>
    //           <Tab label="Register" id="simple-tab-1" aria-controls="simple-tabpanel-1"/>
    //         </Tabs>
    //       </AppBar>
    //       <div
    //         hidden={value !== 0}
    //         id={`simple-tabpanel-0`}
    //         aria-labelledby={`simple-tab-0`}>
    //         <Login/>
    //       </div>
    //       <div
    //         hidden={value !== 1}
    //         id={`simple-tabpanel-1`}
    //         aria-labelledby={`simple-tab-1`}>
    //         <Register />
    //       </div>
    //     </div>
    //   </Grid>
    // </Grid>
  );
};



Home.propTypes = {
  loggedIn: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => {
  return {
    loggedIn: state.loginState.loggedIn,
    error: state.loginState.error,
  };
};

export default connect(mapStateToProps)(Home);
