import React, {useState} from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import HomeOutlinedIcon from "@material-ui/icons/HomeOutlined";
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Container from '@material-ui/core/Container';
import Typography from "@material-ui/core/Typography";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Register from "./Register";
import Login from "./Login";
import HomeIcon from "./HomeIcon.jpg"

const Home = ({ loggedIn }) => {

  const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex'
    },
    img: {
      backgroundImage: `url(${HomeIcon})`,
      position: 'relative',
      height: '100vh',
      width: '100vw',
      opacity: '30%'
    },
    content:{
      position: 'absolute',
      top: '30%',
      left: '50%',
      transform: 'translate(-50%, -30%)'
    },
    hide: {
      display: "none",
    }

  }));
  const classes = useStyles();
  const [value, setValue] = useState(0);

  return (
    <div className={classes.root}>
      <div className={classes.img}/>
      <Container className={classes.content} maxWidth ="xs">
        <Typography variant="h1" gutterBottom>LIST APP</Typography>
        <AppBar position="static">
          <Tabs value={value} onChange={(e, newValue)=>setValue(newValue)} aria-label="tabs" centered>
            <Tab label="Login" id="simple-tab-0" aria-controls="simple-tabpanel-0"/>
            <Tab label="Register" id="simple-tab-1" aria-controls="simple-tabpanel-1"/>
          </Tabs>
        </AppBar>
        <div
          hidden={value !== 0}
          id={`simple-tabpanel-0`}
          aria-labelledby={`simple-tab-0`}>
          <Login/>
        </div>
        <div
          hidden={value !== 1}
          id={`simple-tabpanel-1`}
          aria-labelledby={`simple-tab-1`}>
          <Register />
        </div>
      </Container>
    </div>
  );
};



Home.propTypes = {
  loggedIn: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => {
  return {
    loggedIn: state.loginState.loggedIn,
    error: state.loginState.error,
  };
};

export default connect(mapStateToProps)(Home);
