import React from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import List from "@material-ui/core/List";
import ListItemText from "@material-ui/core/ListItemText";
import { DraggableListItem, ListItemButtons } from "./ListItems";
import { AddDialog } from "../Dialog.js";
import TextField from "@material-ui/core/TextField";

const ListCard = ({ isDraggable, listId, addItem, deleteItem }) => {
  const onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };
  const onDrop = (event, listId, addItem, deleteItem) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("item");
    let selectedItem = this.props.items.find((i) => i._id === itemId);
    if (listId !== event.target._id) {
      //get id of new list
      //THIS IS DEFINITELY WRONG--UPDATE WHEN WE GET TO DOING ITEMS
      const oldList = { listId: listId, ...selectedItem };
      const newList = { listId: event.target._id, ...selectedItem };
      addItem(newList);
      deleteItem(oldList);
    }
  };
  const onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };
  return isDraggable ? (
    <Card
      onDragOver={(event) => onDragOver(event)}
      onDrop={(event) => onDrop(event, listId, addItem, deleteItem)}
      onDragLeave={(event) => onDragLeave(event)}
      style={{ height: "30vw", width: "30vw" }}
    />
  ) : (
    <Card style={{ height: "30vw", width: "30vw" }} />
  );
};

const DraggableList = ({
  isDraggable,
  listId,
  header,
  items,
  addItem,
  editItem,
  deleteItem,
}) => {
  return (
    <Card
      isDraggable={isDraggable || false}
      listId={listId}
      addItem={addItem}
      deleteItem={deleteItem}
    >
      <h4 style={{ margin: "10px 0px" }}>{header}</h4>
      <List>
        <CardContent>
          {items && items.length > 0 ? (
            items.map((item) => {
              return (
                <DraggableListItem
                  key={item._id}
                  isDraggable={isDraggable || false}
                  item={item}
                >
                  <ListItemText primary={item.name} />
                  <ListItemButtons
                    item={item}
                    editItem={editItem}
                    deleteItem={deleteItem}
                  />
                </DraggableListItem>
              );
            })
          ) : (
            <li>No items </li>
          )}
        </CardContent>
      </List>
      <CardActions>
        <AddDialog
          header="Add Item"
          fields={{ "Item Name": "name" }}
          specialFields={[
            (changeFunction) => (
              <TextField
                autoFocus
                margin="dense"
                id="details"
                label="Description"
                fullWidth
                multiline
                required
                type="text"
                onChange={changeFunction}
              />
            ),
          ]}
          addFunction={addItem}
          body={{ listId: listId }}
        />
      </CardActions>
    </Card>
  );
};

export { DraggableList };
