import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import Input from "./Input";
import ListTodo from "./ListTodo";
import { getTodos } from "./TodoReducer";

const progress = ["Pending", "In progress", "Completed"];

class Todo extends Component {
  componentDidMount() {
    this.getTodosFromDB();
  }

  getTodosFromDB = () => {
    axios
      .get("/api/todos")
      .then(res => {
        if (res.data) {
          console.log(res.data);
          this.props.dispatch(getTodos(res.data));
        }
      })
      .catch(err => console.log(err));
  };

  deleteTodo = id => {
    axios
      .delete(`/api/todos/${id}`)
      .then(res => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch(err => console.log(err));
  };

  updateProgress = todo => {
    let progressIndex = progress.indexOf(todo.progress);
    if (progressIndex != 2) {
      let updatedTodo = {
        ...state,
        progress: progressIndex + 1
      };
      updateTodo(updatedTodo);
    }
  };

  updateTodo = body => {
    axios
      .put(`/api/todos/${body._id}`, body)
      .then(res => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch(err => console.log(err));
  };

  render() {
    let { todos } = this.props;
    console.log(todos);
    return (
      <div>
        <h1>My Todo(s)</h1>
        <Input getTodos={this.getTodosFromDB} />
        {progress.map(header => {
          return (
            <ListTodo
              header={header}
              todos={todos}
              deleteTodo={this.deleteTodo}
              updateProgress={this.updateProgress}
            />
          );
        })}
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state.todoState);
  return {
    todos: state.todoState
  };
};

export default connect(mapStateToProps)(Todo);
