import React, { Component } from "react";
import clsx from "clsx";
import { connect } from "react-redux";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import CircularProgress from "@material-ui/core/CircularProgress";
import { withRouter } from "react-router-dom";
import { getSheets, addSheet, deleteSheet } from "../../redux/Sheet";
import { getLists, addList, deleteList } from "../../redux/List";
import { addItem, updateItem, deleteItem, moveItem } from "../../redux/Item";
import Sheet from "./Sheet";
import { AddDialog } from "./Dialog";
import { Home } from "../home";

const drawerWidth = 240;
const styles = (theme) => ({
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    button: {
      margin: theme.spacing(0, 1),
    },
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: "flex-end",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  loggedOutContent: {
    flexGrow: 1,
    padding: theme.spacing(3),
    marginLeft: 0,
  },
});

class Page extends Component {
  state = {
    sheet: undefined,
  };

  componentDidMount() {
    if (this.props.location.pathname !== "/") {
      this.props.getSheets(this.props.pageId);
    }
  }

  changeSheet = (sheetId) => {
    if (
      (this.state.sheet === undefined &&
        sheetId !== this.props.sheets[0]._id) ||
      (this.state.sheet && sheetId !== this.state.sheet._id)
    ) {
      const sheet = this.props.sheets.find((sheet) => {
        return sheet._id === sheetId;
      });
      this.setState({ sheet });
      this.props.getLists(sheetId);
    }
  };

  render() {
    const { classes } = this.props;
    return (
      <main
        className={clsx({
          [classes.loggedOutContent]: !this.props.loggedIn,
          [classes.content]: this.props.loggedIn,
          [classes.contentShift]: this.props.open,
        })}
      >
        <div className={classes.drawerHeader} />
        {this.props.location.pathname === "/" ? (
          <Home />
        ) : (
          // {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
          <div>
            {/* {this.props.sheets && this.props.sheets.length > 0 ? (
              <>
                <AppBar
                  color="transparent"
                  position="relative"
                  className={classes.appBar}
                >
                  <Toolbar>
                    {this.props.sheets.map((sheet) => (
                      <Button
                        className={classes.appBar.button}
                        variant="outlined"
                        key={sheet._id}
                        onClick={() => this.changeSheet(sheet._id)}
                      >
                        {sheet.name}
                      </Button>
                    ))}

                    <AddDialog
                      header="Add Sheet"
                      fields={{ "Sheet Name": "name" }}
                      addFunction={this.props.addSheet}
                      body={{ pageId: this.props.pageId }}
                    />
                  </Toolbar>
                </AppBar>
                <Sheet
                  pageId={this.props.pageId}
                  sheet={this.state.sheet || this.props.sheets[0]}
                  isDraggable={true}
                  deleteSheet={this.props.deleteSheet}
                  lists={this.props.lists}
                  addList={this.props.addList}
                  deleteList={this.props.deleteList}
                  moveItem={this.props.moveItem}
                  addItem={this.props.addItem}
                  editItem={this.props.updateItem}
                  deleteItem={this.props.deleteItem}
                  loading={this.props.listLoading}
                  itemLoading={this.props.itemLoading}
                />
              </>
            ) : ( */}
            <div style={{ marginTop: "50vh" }}>
              {this.props.loading ? (
                <CircularProgress />
              ) : (
                <Typography align="center" variant="h1">
                  Sheets do not yet exist
                </Typography>
              )}
            </div>
            {/* )} */}
          </div>
        )}
      </main>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    loggedIn: state.loginState.loggedIn,
    sheets: state.sheetState.sheets,
    lists: state.listState.lists,
    open: state.uiState.menuOpen,
    loading: state.sheetState.loading,
    listLoading: state.listState.loading,
    itemLoading: state.itemState.loading,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  deleteSheet,
  getLists,
  addList,
  deleteList,
  moveItem,
  addItem,
  updateItem,
  deleteItem,
};

const styledPage = withStyles(styles, { withTheme: true })(Page);
export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(styledPage)
);
