import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import AppBar from "@material-ui/core/AppBar";
import Button from "@material-ui/core/Button";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import HomeIcon from "@material-ui/icons/Home";
import PlaylistAddCheckIcon from "@material-ui/icons/PlaylistAddCheck";
import AccountCircle from "@material-ui/icons/AccountCircle";
import { login, logout } from "../home/ducks/Login";

const LoggedInNav = ({ handleLogout, handleRedirect, handleHome }) => {
  return (
    <Toolbar>
      <IconButton>
        <HomeIcon color="secondary" onClick={handleHome} />
      </IconButton>
      <IconButton>
        <PlaylistAddCheckIcon color="secondary" onClick={handleRedirect} />
      </IconButton>
      <IconButton>
        <AccountCircle color="secondary" />
      </IconButton>
      <Button
        style={{ position: "absolute", right: "50px" }}
        variant="contained"
        onClick={handleLogout}
      >
        Logout
      </Button>
    </Toolbar>
  );
};

const LoggedOutNav = ({ handleLogin, handleRedirect }) => {
  return (
    <Toolbar>
      <Button variant="contained" onClick={handleLogin}>
        Admin Login
      </Button>
      <Button color="inherit">Login</Button>
    </Toolbar>
  );
};

class NavBar extends Component {
  handleLogin = () => {
    this.props.login("tester", "pass", this.props.history);
  };
  handleLogout = () => {
    this.props.logout(this.props.history);
  };
  handleRedirect = (route) => {
    this.props.history.push(route);
  };

  render() {
    return (
      <AppBar position="static">
        {this.props.loggedIn ? (
          <LoggedInNav
            handleLogout={this.handleLogout}
            handleRedirect={this.handleRedirect(`/todo`)}
            handleHome={this.handleRedirect(`/`)}
          />
        ) : (
          <LoggedOutNav
            handleLogin={this.handleLogin}
            handleRedirect={this.handleRedirect}
          />
        )}
      </AppBar>
    );
  }
}
const mapStateToProps = (state) => {
  console.log(state.loginState);
  // console.log(state.registerState);
  return {
    loggingIn: state.loginState.loggingIn,
    loggedIn: state.loginState.loggedIn,
    logout: state.loginState.logout,
    //   registered: state.registerState.registered,
    user: state.loginState.user,
    error: state.loginState.error,
  };
};

const mapDispatchToProps = {
  login,
  logout,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(NavBar));
