import React from "react";
import Grid from "@material-ui/core/Grid";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({ sheet, lists, addList, ...rest }) => {
  return (
    <Grid
      container
      spacing={1}
      direction="row"
      justify="space-evenly"
      alignItems="flex-start"
      style={{ padding: "10px" }}
    >
      {lists && lists.length > 0 ? (
        lists.map((list) => {
          return (
            <Grid item key={list._id}>
              <DraggableList
                listId={list._id}
                header={list.name}
                items={list.items}
                {...rest}
              />
            </Grid>
          );
        })
      ) : (
        <h1>Sheet does not yet exist</h1>
      )}
      <AddDialog
        header="Add List"
        fields={{ "List Name": "name" }}
        addFunction={addList}
        state={{ pageId: sheet._id }}
      />
    </Grid>
  );
};

export default Sheet;
