import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";
import Input from "./Input";
import ListTodo from "./ListTodo";
import { getTodos } from "./TodoReducer";
import Grid from "@material-ui/core/Grid";

const progress = ["Pending", "In progress", "Completed"];

class Todo extends Component {
  componentDidMount() {
    this.getTodosFromDB();
  }

  getTodosFromDB = () => {
    axios
      .get("/api/todos")
      .then(res => {
        if (res.data) {
          console.log(res.data);
          this.props.dispatch(getTodos(res.data));
        }
      })
      .catch(err => console.log(err));
  };

  deleteTodo = id => {
    axios
      .delete(`/api/todos/${id}`)
      .then(res => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch(err => console.log(err));
  };

  updateProgress = todo => {
    let progressIndex = progress.indexOf(todo.progress);
    if (progressIndex !== 2) {
      let updatedTodo = {
        ...todo,
        progress: progress[progressIndex + 1]
      };
      this.updateTodo(updatedTodo);
    }
  };

  updateTodo = body => {
    console.log(body);
    axios
      .put(`/api/todos/${body._id}`, body)
      .then(res => {
        if (res.data) {
          this.getTodosFromDB();
        }
      })
      .catch(err => console.log(err));
  };

  onDragStart = (event, todo) => {
    console.log("dragstart on div: ", todo);
    event.currentTarget.style.border = "dashed";
    event.dataTransfer.setData("todo", todo);
  };
  onDragOver = event => {
    event.preventDefault();
  };

  onDrop = (event, progressState) => {
    event.preventDefault();
    let todoId = event.dataTransfer.getData("todo");
    let todoItem = this.props.todos.find(i => i._id === todoId);
    let progressIndex = progress.indexOf(todoItem.progress);
    let newProgressIndex = progress.indexOf(progressState);
    if (progressIndex !== newProgressIndex) {
      let updatedTodo = {
        ...todoItem,
        progress: progress[newProgressIndex]
      };
      console.log(updatedTodo);
      deleteTodo(updatedTodo);
    }
  };

  render() {
    let { todos } = this.props;
    console.log(todos);
    return (
      <div>
        <h1>My Todo(s)</h1>
        <Input getTodos={this.getTodosFromDB} />
        <Grid
          container
          spacing={1}
          direction="row"
          justify="space-evenly"
          alignItems="flex-start"
        >
          {progress.map(header => {
            return (
              <ListTodo
                key={progress.indexOf(header)}
                header={header}
                todos={todos}
                deleteTodo={this.deleteTodo}
                updateProgress={this.updateProgress}
                onDrop={this.onDrop}
                onDragOver={this.onDragOver}
                onDragStart={this.onDragStart}
              />
            );
          })}
        </Grid>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state.todoState);
  return {
    todos: state.todoState
  };
};

export default connect(mapStateToProps)(Todo);
