import React, { useState, useCallback } from "react";
import Grid from "@material-ui/core/Grid";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({ sheet, lists, addList, ...rest }) => {
  const [bottomItem, setBottomItem] = useState(0);

  // const wrapperSetParentState = useCallback(
  //   (val) => {
  //     setBottomItem(val);
  //   },
  //   [setBottomItem]
  // );
  const onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };

  const onDrop = (event, listId, items, moveItem, sheetId) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("item");
    console.log(itemId);
    console.log(bottomItem);
    let index =
      bottomItem !== items.length &&
      bottomItem !== 0 &&
      items.findIndex((i) => i._id === bottomItem) !== -1
        ? items.findIndex((i) => i._id === bottomItem)
        : bottomItem;
    console.log(index);
    let body = { listId, index, itemId };
    moveItem(body, sheetId);
  };

  const onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };

  // let bottomItem = 0; //problem moving between lists bc this is per list
  const onDragOverItem = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightblue";
  };

  const onDropItem = (event, item) => {
    console.log(item);
    event.preventDefault();
    event.currentTarget.style.background = "";
    if (item !== bottomItem) {
      setBottomItem(item);
    }
  };

  const onDragLeaveItem = (event) => {
    event.currentTarget.style.background = "";
  };

  return (
    <Grid
      container
      spacing={1}
      direction="row"
      justify="space-evenly"
      alignItems="flex-start"
      style={{ padding: "10px" }}
    >
      {lists && lists.length > 0 ? (
        lists.map((list) => {
          return (
            <Grid item key={list._id}>
              <h5>{list._id}</h5>
              <DraggableList
                sheetId={sheet._id}
                listId={list._id}
                header={list.name}
                items={list.items}
                //doesn't work since it's making separate coppies
                // bottomItem={bottomItem}
                // setBottomItem={wrapperSetParentState}
                onDragOver={onDragOver}
                onDrop={onDrop}
                onDragOverItem={onDragOverItem}
                onDragLeave={onDragLeave}
                onDropItem={onDropItem}
                onDragLeaveItem={onDragLeaveItem}
                {...rest}
              />
            </Grid>
          );
        })
      ) : (
        <h1> Lists do not yet exist </h1>
      )}
      <AddDialog
        header="Add List"
        fields={{ "List Name": "name" }}
        addFunction={addList}
        body={{ pageId: sheet._id }}
      />
    </Grid>
  );
};

export default Sheet;
