import React from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";

const ListTodo = ({ header, todos, deleteTodo, updateProgress }) => {
  return (
    <div>
      <h4>{header}</h4>
      <Grid container item direction="column" xs={12} spacing={3}>
        {todos && todos.length > 0 ? (
          todos
            .filter(todo => todo.progress === header)
            .map(todo => {
              return (
                <Grid item key={todo._id} onClick={() => updateProgress(todo)}>
                  {todo.action}
                  <IconButton
                    color="primary"
                    aria-label="delete"
                    component="span"
                  >
                    <DeleteIcon onClick={() => deleteTodo(todo._id)} />
                  </IconButton>
                </Grid>
              );
            })
        ) : (
          <li>No todo(s) left</li>
        )}
      </Grid>
    </div>
  );
};

export default ListTodo;
