import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "./Dialog";
import { DraggableList } from "./List/List";

const Sheet = ({ pageId, sheet, lists, addList, deleteSheet, ...rest }) => {
  const useStyles = makeStyles((theme) => ({
    root: { padding: "10px" },
    gridItem: {
      height: "30vw",
      width: "30vw",
      // transition: theme.transitions.create(["margin", "width"], {
      //   easing: theme.transitions.easing.sharp,
      //   duration: theme.transitions.duration.leavingScreen,
      // }),
    },
    // appBarShift: {
    //   width: `calc(100% - ${drawerWidth}px)`,
    //   marginLeft: drawerWidth,
    //   transition: theme.transitions.create(["margin", "width"], {
    //     easing: theme.transitions.easing.easeOut,
    //     duration: theme.transitions.duration.enteringScreen,
    //   }),
    // },
    addButton: {
      marginRight: theme.spacing(2),
    },
    hide: {
      display: "none",
    },
  }));

  const classes = useStyles();

  return (
    <Grid
      container
      spacing={1}
      direction="row"
      justify="space-evenly"
      alignItems="flex-start"
      className={classes.root}
    >
      {lists && lists.length > 0 ? (
        lists.map((list) => {
          return (
            <Grid item key={list._id} className={classes.gridItem}>
              <DraggableList
                sheetId={sheet._id}
                listId={list._id}
                header={list.name}
                items={list.items}
                {...rest}
              />
            </Grid>
          );
        })
      ) : (
        <h1> Lists do not yet exist </h1>
      )}
      <AddDialog
        header="Add List"
        fields={{ "List Name": "name" }}
        addFunction={addList}
        body={{ pageId: sheet._id }}
        className={classes.addButton}
      />
      <IconButton
        edge="end"
        aria-label="delete"
        onClick={() => deleteSheet({ pageId, sheetId: sheet._id })}
      >
        <CloseIcon />
      </IconButton>
    </Grid>
  );
};

export default Sheet;
