//   };
//   return isDraggable ? (
//     <Card
//       onDragOver={(event) => onDragOver(event)}
//       onDrop={(event) => onDrop(event, listId, addItem, deleteItem)}
//       onDragLeave={(event) => onDragLeave(event)}
//       style={{ height: "30vw", width: "30vw" }}
//     >
//       {rest}
//     </Card>
//   ) : (
//     <Card style={{ height: "30vw", width: "30vw" }}>{rest}</Card>
//   );
// };

const DraggableList = ({
  isDraggable,
  sheetId,
  listId,
  header,
  items,
  moveLists,
  addItem,
  editItem,
  deleteItem,
}) => {
  const onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };
  const onDrop = (event, listId, items, moveLists) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("item");
    console.log(itemId);
    //find index of item it's dragged over, use as index to insert
    //if list is empty, index = 0
    //let index = items.find((i) => i._id === itemId);

    // moveLists(listId, index, itemId);
  };

  const onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };

  const onDragOverItem = (event, item) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightblue";
    if (
      !event.dataTransfer.getData("itemOnBottom") ||
      (event.dataTransfer.getData("itemOnBottom") &&
        item !== event.dataTransfer.getData("itemOnBottom"))
    ) {
      event.dataTransfer.setData("itemOnBottom", item);
      console.log(item);
      console.log(event.dataTransfer.getData("itemOnBottom"));
    }
  };

  const onDropItem = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("itemOnBottom");
    console.log(itemId);
  };

  const onDragLeaveItem = (event) => {
    event.currentTarget.style.background = "";
  };

  return (
    <Card
      onDragOver={(event) => onDragOver(event)}
      onDrop={(event) => onDrop(event, listId, items, moveLists)}
      onDragLeave={(event) => onDragLeave(event)}
      style={{ height: "30vw", width: "30vw" }}
    >
      {/* // <ListCard
    //   isDraggable={isDraggable || false}
    //   listId={listId}
    //   addItem={addItem}
    //   deleteItem={deleteItem}
    // > */}
      <h4 style={{ margin: "10px 0px" }}>{header}</h4>
      <List>
        <CardContent>
          {items && items.length > 0 ? (
            items.map((item) => {
              return (
                <DraggableListItem
                  key={item._id}
                  isDraggable
                  // isDraggable={isDraggable || false}
                  item={item}
                  onDragOver={(event) => onDragOverItem(event, item._id)}
                  onDrop={(event) => onDropItem(event)}
                  onDragLeave={(event) => onDragLeaveItem(event)}
                >
                  <ListItemText primary={item.name} />
                  {/* <ListItemButtons
                    item={item}
                    editItem={editItem}
                    deleteItem={deleteItem}
                  /> */}
                </DraggableListItem>
              );
            })
          ) : (
            <li>No items </li>
          )}
        </CardContent>
      </List>
      <CardActions>
        <AddDialog
          header="Add Item"
          fields={{ "Item Name": "name" }}
          // specialFields={[
          //   (changeFunction) => {
          //     return (
          //       <TextField
          //         margin="dense"
          //         key="Description"
          //         id="details"
          //         label="Description"
          //         fullWidth
          //         multiline
          //         required
          //         type="text"
          //         onChange={changeFunction}
          //       />
          //     );
          //   },
          // ]}
          addFunction={addItem}
          body={{ listId: listId, sheetId: sheetId }}
        />
      </CardActions>
      {/* </ListCard> */}
    </Card>
  );
};

export { DraggableList };
