import React, { Component } from "react";
import { connect } from "react-redux";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import { withRouter } from "react-router-dom";
import { getSheets, addSheet } from "../../redux/Sheet";
import { addList, updateList } from "../../redux/List";
import { addItem, updateItem, deleteItem } from "../../redux/Item";
import Sheet from "./Sheet";
import AddDialog from "./Dialog";

class Page extends Component {
  componentDidMount() {
    this.props.getSheets(this.props.pageId);
  }

  // handleChange = (e) => {
  //   this.setState({ [e.target.id]: e.target.value });
  // };

  render() {
    return (
      <div>
        {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
        <AppBar>
          <Toolbar>
            {this.props.sheets
              ? this.props.sheets.map((sheet) => {
                  return <Button variant="outlined">{sheet.name}</Button>;
                })
              : null}

            <AddDialog
              header="Add Sheet"
              fields={{ "Sheet Name": "name" }}
              addFunction={this.props.addSheet}
              state={{ pageId: this.props.pageId }}
            />
          </Toolbar>
        </AppBar>
        {this.props.sheets ? (
          <Sheet
            sheet={this.props.sheets[0]}
            isDraggable={true}
            lists={this.props.lists}
            addList={this.props.addList}
            editList={this.props.updateList}
            addItem={this.props.addItem}
            editItem={this.props.updateItem}
            deleteItem={this.props.deleteItem}
            // handleChange={this.handleChange}
          />
        ) : (
          <h1>Worksheets do not yet exist</h1>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  // console.log(state.loginState);
  return {
    sheets: this.sheetState.sheets,
    lists: this.listState.lists,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  addList,
  updateList,
  addItem,
  updateItem,
  deleteItem,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Page));
