class Page extends Component {
  state = {
    sheet: undefined,
  };

  componentDidMount() {
    this.props.getSheets(this.props.pageId);
  }

  changeSheet = (sheetId) => {
    if (
      (this.state.sheet === undefined &&
        sheetId !== this.props.sheets[0]._id) ||
      (this.state.sheet && sheetId !== this.state.sheet._id)
    ) {
      const sheet = this.props.sheets.find((sheet) => {
        return sheet._id === sheetId;
      });
      this.setState({ sheet });
      this.props.getLists(sheetId);
    }
  };

  render() {
    return (
      <div>
        <NavBar />
        {this.props.pageType === "Home" ? (
          <Home />
        ) : (
          // {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
          <>
            <AppBar color="transparent" style={{ marginTop: "10vh" }}>
              <Toolbar>
                {this.props.sheets
                  ? this.props.sheets.map((sheet) => (
                      <Button
                        variant="outlined"
                        key={sheet._id}
                        onClick={() => this.changeSheet(sheet._id)}
                      >
                        {sheet.name}
                      </Button>
                    ))
                  : null}

                <AddDialog
                  header="Add Sheet"
                  fields={{ "Sheet Name": "name" }}
                  addFunction={this.props.addSheet}
                  body={{ pageId: this.props.pageId }}
                />
              </Toolbar>
            </AppBar>
            {this.props.sheets && this.props.sheets.length > 0 ? (
              <Sheet
                pageId={this.props.pageId}
                sheet={this.state.sheet || this.props.sheets[0]}
                isDraggable={true}
                deleteSheet={this.props.deleteSheet}
                lists={this.props.lists}
                addList={this.props.addList}
                deleteList={this.props.deleteList}
                moveItem={this.props.moveItem}
                addItem={this.props.addItem}
                editItem={this.props.updateItem}
                deleteItem={this.props.deleteItem}
              />
            ) : (
              <h1>Sheets do not yet exist</h1>
            )}
          </>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log(state.sheetState);
  console.log(state.listState);
  return {
    sheets: state.sheetState.sheets,
    lists: state.listState.lists,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  deleteSheet,
  getLists,
  addList,
  deleteList,
  moveItem,
  addItem,
  updateItem,
  deleteItem,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Page));
