import React from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import AppBar from "@material-ui/core/AppBar";
import { LoggedInNav, LoggedOutNav } from "./NavViews";
import { login, logout } from "../../redux/Login";
import { addPage } from "../../redux/Page";

const NavBar = () => {
  handleLogin = () => {
    this.props.login("tester", "pass", this.props.history);
  };
  handleLogout = () => {
    this.props.logout(this.props.history);
  };
  handleRedirect = (route) => {
    this.props.history.push(`/${route}`);
  };
  handleHome = () => {
    this.props.history.push(`/`);
  };

  // addPage = () => {
  //   console.log("add placeholder");
  // };

  return (
    <AppBar position="static">
      {this.props.loggedIn ? (
        <LoggedInNav
          menu={this.props.menu}
          handleLogout={this.handleLogout}
          handleRedirect={this.handleRedirect}
          handleHome={this.handleHome}
          addPage={this.props.addPage}
          state={{ username: this.props.user }}
        />
      ) : (
        <LoggedOutNav
          handleLogin={this.handleLogin}
          handleRedirect={this.handleRedirect}
        />
      )}
    </AppBar>
  );
};
const mapStateToProps = (state) => {
  console.log(state.loginState);
  return {
    loggingIn: state.loginState.loggingIn,
    loggedIn: state.loginState.loggedIn,
    logout: state.loginState.logout,
    user: state.loginState.user,
    error: state.pageState.error,
    menu: state.pageState.menu,
  };
};

const mapDispatchToProps = {
  login,
  logout,
  addPage,
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(NavBar));
