import React from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";

const ListTodo = ({ header, todos, deleteTodo, updateProgress }) => {
  return (
    <div>
      <h4>{header}</h4>
      <Grid container item direction="column" xs={12} spacing={3}>
        {todos && todos.length > 0 ? (
          todos
            .filter(todo => todo.progress === header)
            .map(todo => {
              return (
                <List key={todo._id} onClick={() => updateProgress(todo)}>
                  <ListItem>
                    <ListItemText primary={todo.action} />
                    <ListItemSecondaryAction>
                      <IconButton edge="end" aria-label="delete">
                        <DeleteIcon onClick={() => deleteTodo(todo._id)} />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                </List>
              );
            })
        ) : (
          <li>No todo(s) left</li>
        )}
      </Grid>
    </div>
  );
};

export default ListTodo;
