import React from "react";

const ListTodo = ({ header, todos, deleteTodo, updateProgress }) => {
  return (
    <div>
      <h1>{header}</h1>
      <ul>
        {todos && todos.length > 0 ? (
          todos
            .filter(todo => todo.progress == header)
            .map(todo => {
              return (
                <li key={todo._id} onClick={() => updateProgress(todo._id)}>
                  {todo.action}{" "}
                  <span onClick={() => deleteTodo(todo._id)}> X </span>
                </li>
              );
            })
        ) : (
          <li>No todo(s) left</li>
        )}
      </ul>
    </div>
  );
};

export default ListTodo;
