import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import HomeIcon from "@material-ui/icons/Home";
import MenuIcon from "@material-ui/icons/Menu";
import CloseIcon from "@material-ui/icons/Close";
import { AddDialog } from "../common/Dialog";

export const LoggedInNav = ({
  pages,
  handleLogout,
  handleRedirect,
  handleHome,
  addPage,
  deletePage,
  username,
}) => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Toolbar variant="dense">
      <IconButton
        edge="start"
        color="inherit"
        aria-label="menu"
        onClick={handleClick}
      >
        <MenuIcon />
      </IconButton>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        {pages &&
          pages.map((page) => {
            return (
              <MenuItem
                key={page._id}
                onClick={() => handleRedirect(page.name)}
              >
                {page.name}
                <IconButton
                  edge="end"
                  aria-label="delete"
                  onClick={() => deletePage({ username, pageId: page._id })}
                >
                  <CloseIcon />
                </IconButton>
              </MenuItem>
            );
          })}
        {/* <MenuItem onClick={addPage}>Add page</MenuItem> */}
        <MenuItem>
          <AddDialog
            header="Add Page"
            fields={{ "Page Name": "name" }}
            addFunction={addPage}
            // body={state}
          />
        </MenuItem>
      </Menu>
      <IconButton onClick={handleHome}>
        <HomeIcon color="secondary" />
      </IconButton>
      <Button
        style={{ position: "absolute", right: "50px" }}
        variant="contained"
        onClick={handleLogout}
      >
        Logout
      </Button>
    </Toolbar>
  );
};

export const LoggedOutNav = ({ handleLogin }) => {
  return (
    <Toolbar variant="dense">
      <Button variant="contained" onClick={handleLogin}>
        Admin Login
      </Button>
      <Button color="inherit">Login</Button>
    </Toolbar>
  );
};
