import React, { Component } from "react";
import PropTypes from "prop-types";
import clsx from "clsx";
import { connect } from "react-redux";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import CircularProgress from "@material-ui/core/CircularProgress";
import { withRouter } from "react-router-dom";
import { getSheets, addSheet, editSheet, deleteSheet } from "../../redux/Sheet";
import { getLists, addList, deleteList } from "../../redux/List";
import { addItem, editItem, deleteItem, moveItem } from "../../redux/Item";
import Sheet from "./Sheet";
import { AddDialog } from "./Dialog";
import { Home } from "../home";
import { EditableListItem } from "./List/ListItems";

const drawerWidth = 240;
const styles = (theme) => ({
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    button: {
      margin: theme.spacing(0, 1),
    },
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: "flex-end",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  loggedOutContent: {
    flexGrow: 1,
    padding: theme.spacing(3),
    marginLeft: 0,
  },
});

class Page extends Component {
  state = {
    sheet: undefined,
    editClicked: false,
  };

  componentDidMount() {
    if (this.props.location.pathname !== "/") {
      this.props.getSheets(this.props.pageId);
    }
  }
  setEditClicked = () => {
    this.setState({ editClicked: !editClicked });
  };

  changeSheet = (sheetId) => {
    if (
      (this.state.sheet === undefined &&
        sheetId !== this.props.sheets[0]._id) ||
      (this.state.sheet && sheetId !== this.state.sheet._id)
    ) {
      const sheet = this.props.sheets.find((sheet) => {
        return sheet._id === sheetId;
      });
      this.setState({ sheet });
      this.props.getLists(sheetId);
    }
  };

  render() {
    const { classes } = this.props;
    return (
      <main
        className={clsx({
          [classes.loggedOutContent]: !this.props.loggedIn,
          [classes.content]: this.props.loggedIn,
          [classes.contentShift]: this.props.open,
        })}
      >
        <div className={classes.drawerHeader} />
        {this.props.location.pathname === "/" ? (
          <Home />
        ) : (
          // {/* make toolbar scrollable (gridlist/scrollable tabs?) if it exceeds width of screen */}
          <div>
            {this.props.sheets && this.props.sheets.length > 0 ? (
              <>
                <AppBar
                  color="transparent"
                  position="relative"
                  className={classes.appBar}
                >
                  <Toolbar>
                    {this.props.sheets.map((sheet) => (
                      <Button
                        className={classes.appBar.button}
                        variant="outlined"
                        key={sheet._id}
                        onClick={() => this.changeSheet(sheet._id)}
                      >
                        <EditableListItem
                          key={sheet._id}
                          editMode={editClicked}
                          item={sheet}
                          editFunction={(inputText) =>
                            editSheet({
                              id: sheet._id,
                              pageId: this.props.pageId,
                              name: inputText,
                            })
                          }
                          deleteFunction={() =>
                            deleteSheet({
                              sheetId: sheet._id,
                              pageId: this.props.pageId,
                            })
                          }
                          deleteIcon={
                            <IconButton
                              edge="end"
                              aria-label="delete"
                              onClick={() =>
                                deleteSheet({
                                  sheetId: sheet._id,
                                  pageId: this.props.pageId,
                                })
                              }
                            >
                              <CloseIcon />
                            </IconButton>
                          }
                          // handleRedirect={() => this.changeSheet(sheet._id)}
                          // pathname={pathname}
                        />
                        // {sheet.name}
                      </Button>
                    ))}
                    <div style={{ position: "absolute", right: "50px" }}>
                      <IconButton
                        edge="end"
                        aria-label="edit"
                        onClick={() => setEditClicked(!this.state.editClicked)}
                      >
                        <EditIcon />
                      </IconButton>
                      <AddDialog
                        header="Add Sheet"
                        fields={{ "Sheet Name": "name" }}
                        addFunction={this.props.addSheet}
                        body={{ pageId: this.props.pageId }}
                      />
                    </div>
                  </Toolbar>
                </AppBar>
                <Sheet
                  pageId={this.props.pageId}
                  sheet={this.state.sheet || this.props.sheets[0]}
                  isDraggable={true}
                  deleteSheet={this.props.deleteSheet}
                  lists={this.props.lists}
                  items={this.props.items}
                  addList={this.props.addList}
                  deleteList={this.props.deleteList}
                  moveItem={this.props.moveItem}
                  addItem={this.props.addItem}
                  editItem={this.props.editItem}
                  deleteItem={this.props.deleteItem}
                  loading={this.props.listLoading}
                  itemLoading={this.props.itemLoading}
                  listIdLoading={this.props.listIdLoading}
                />
              </>
            ) : (
              <div style={{ marginTop: "30vh" }}>
                {this.props.loading ? (
                  <CircularProgress />
                ) : (
                  <Typography align="center" variant="h3">
                    Sheets do not yet exist
                  </Typography>
                )}
              </div>
            )}
          </div>
        )}
      </main>
    );
  }
}
Page.propTypes = {
  location: PropTypes.shape({
    hash: PropTypes.string.isRequired,
    key: PropTypes.string,
    pathname: PropTypes.string.isRequired,
    search: PropTypes.string.isRequired,
    state: PropTypes.node,
  }).isRequired,
  pageId: PropTypes.string,
  sheets: PropTypes.arrayOf(
    PropTypes.shape({
      _id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      sheets: PropTypes.array.isRequired,
      items: PropTypes.array.isRequired,
    })
  ).isRequired,
  listLoading: PropTypes.bool.isRequired,
  itemLoading: PropTypes.bool.isRequired,
  loading: PropTypes.bool.isRequired,
  listIdLoading: PropTypes.arrayOf(PropTypes.string).isRequired,
  loggedIn: PropTypes.bool.isRequired,
  open: PropTypes.bool.isRequired,

  getSheets: PropTypes.func.isRequired,
  addSheet: PropTypes.func.isRequired,
  editSheet: PropTypes.func.isRequired,
  deleteSheet: PropTypes.func.isRequired,
  getLists: PropTypes.func.isRequired,
  addList: PropTypes.func.isRequired,
  deleteList: PropTypes.func.isRequired,
  moveItem: PropTypes.func.isRequired,
  addItem: PropTypes.func.isRequired,
  editItem: PropTypes.func.isRequired,
  deleteItem: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => {
  // console.log(state.sheetState);
  // console.log(state.itemState);
  return {
    loggedIn: state.loginState.loggedIn,
    sheets: state.sheetState.sheets,
    lists: state.listState.lists,
    items: state.itemState.items,
    open: state.uiState.menuOpen,
    loading: state.sheetState.loading,
    listLoading: state.listState.loading,
    itemLoading: state.itemState.loading,
    listIdLoading: state.itemState.listIdLoading,
  };
};

const mapDispatchToProps = {
  getSheets,
  addSheet,
  editSheet,
  deleteSheet,
  getLists,
  addList,
  deleteList,
  moveItem,
  addItem,
  editItem,
  deleteItem,
};

const styledPage = withStyles(styles, { withTheme: true })(Page);
export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(styledPage)
);
