import { pageService } from "../services/PageService";

//Actions
const PAGE_REQUEST = "components/common/PAGE_REQUEST";
const PAGE_SUCCESS = "components/common/PAGE_SUCCESS";
const PAGE_FAILURE = "components/common/PAGE_FAILURE";

//Reducer

export default function pageReducer(state = {}, action) {
  switch (action.type) {
    case PAGE_REQUEST:
      return Object.assign({}, state, {
        gettingPages: true,
      });
    case PAGE_SUCCESS:
      return Object.assign({}, state, {
        pages: action.pages.pages,
        // menu: action.pages.menu,
      });
    case PAGE_FAILURE:
      return Object.assign({}, state, {
        error: true,
      });
    // case LOGOUT:
    //   return Object.assign({}, state, {
    //     logout: true,
    //   });
    default:
      return state;
  }
}

//Action Creators
export function getPages() {
  return (dispatch) => {
    dispatch(request());
    pageService.getPages().then(
      (pages) => {
        // dispatch(success({ pages }));
        console.log(pages);
        if (pages) {
          let menu = [];
          pages.forEach((element) => menu.push(element["name"]));
          dispatch(success({ pages, menu }));
        }
      },
      (error) => {
        console.log(error);
        dispatch(failure(error.toString()));
        // dispatch(alertActions.error(error.toString()));
      }
    );
  };

  function request() {
    return { type: PAGE_REQUEST };
  }
  function success(pages) {
    return { type: PAGE_SUCCESS, pages };
  }
  function failure(error) {
    return { type: PAGE_FAILURE, error };
  }
}

function _handlePageAction(apiCall, body) {
  return (dispatch) => {
    dispatch({ type: PAGE_REQUEST });
    apiCall(body).then(
      (pages) => {
        console.log(pages);
        // dispatch({ type: PAGE_SUCCESS, pages });
        dispatch(getPages());
      },
      (error) => {
        dispatch({ type: PAGE_FAILURE, error });
      }
    );
  };
}

//check what is being taken as body/necessary params
export function addPage(body) {
  return _handlePageAction(pageService.createPage, body);
}
export function deletePage(id) {
  return _handlePageAction(pageService.deletePage, id);
}
export function updatePage(body) {
  return _handlePageAction(pageService.update, body);
}
