import axios from "axios";
import { authHeader, handleResponse } from "./ServiceHelpers";

export const todoService = {
  getTodos,
  addTodo,
  deleteTodo,
  updateTodo,
};

const requestOptions = {
  headers: { ...authHeader(), "Content-Type": "application/json" },
};

function getTodos() {
  return axios
    .get("/todos/getTodos", requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function addTodo(todo) {
  return axios
    .post("/todos/createTodo", todo, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function deleteTodo(id) {
  return axios
    .delete(`/todos/deleteTodo/${id}`, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}

function updateTodo(body) {
  return axios
    .put(`/todos/updateTodo/${body._id}`, body, requestOptions)
    .then(handleResponse)
    .catch((err) => console.log(err));
}
