import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import HomeIcon from "@material-ui/icons/Home";
import MenuIcon from "@material-ui/icons/Menu";
import PlaylistAddCheckIcon from "@material-ui/icons/PlaylistAddCheck";
import AccountCircle from "@material-ui/icons/AccountCircle";
import { AddDialog } from "../common/Dialog";

export const LoggedInNav = ({
  menu,
  handleLogout,
  handleRedirect,
  handleHome,
  addPage,
  state,
}) => {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Toolbar variant="dense">
      <IconButton
        edge="start"
        color="inherit"
        aria-label="menu"
        onClick={handleClick}
      >
        <MenuIcon />
      </IconButton>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        {/* handle the redirect on manu item click and the add page */}
        {menu.menu.map((i) => {
          return <MenuItem onClick={handleRedirect(i)}>{i}</MenuItem>;
        })}
        {/* <MenuItem onClick={addPage}>Add page</MenuItem> */}
        <AddDialog
          header="Add Page"
          fields={{ "Page Name": "name" }}
          addFunction={addPage}
          body={state}
        />
      </Menu>
      <IconButton>
        <HomeIcon color="secondary" onClick={handleHome} />
      </IconButton>
      {/* <IconButton>
          <PlaylistAddCheckIcon color="secondary" onClick={handleRedirect} />
        </IconButton>
        <IconButton>
          <AccountCircle color="secondary" />
        </IconButton> */}
      <Button
        style={{ position: "absolute", right: "50px" }}
        variant="contained"
        onClick={handleLogout}
      >
        Logout
      </Button>
    </Toolbar>
  );
};

export const LoggedOutNav = ({ handleLogin }) => {
  return (
    <Toolbar variant="dense">
      <Button variant="contained" onClick={handleLogin}>
        Admin Login
      </Button>
      <Button color="inherit">Login</Button>
    </Toolbar>
  );
};
