import React from "react";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import List from "@material-ui/core/List";
import { DraggableListItem, ListItemButtons } from "./ListItems";
import { AddDialog } from "../Dialog.js";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";

const DraggableList = ({
  isDraggable,
  sheetId,
  listId,
  header,
  items,
  deleteList,
  moveItem,
  addItem,
  editItem,
  deleteItem,
}) => {
  let bottomItem = 0;

  function setBottomItem(newItem) {
    bottomItem = newItem;
  }

  const onDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightgray";
    // event.currentTarget.style.cursor = "grabbing";
  };

  const onDrop = (event, listId, items, moveItem, sheetId, bottomItem) => {
    event.preventDefault();
    event.currentTarget.style.background = "";
    // event.currentTarget.style.cursor = "default";
    let itemId = event.dataTransfer.getData("item");
    console.log(itemId);
    console.log(bottomItem);
    let index =
      bottomItem !== items.length &&
      bottomItem !== 0 &&
      items.findIndex((i) => i._id === bottomItem) !== -1
        ? items.findIndex((i) => i._id === bottomItem)
        : bottomItem;
    console.log(index);
    let body = { listId, index, itemId };
    moveItem(body, sheetId);
  };

  const onDragLeave = (event) => {
    event.currentTarget.style.background = "";
  };

  const onDragOverItem = (event) => {
    event.preventDefault();
    event.currentTarget.style.background = "lightblue";
  };

  const onDropItem = (event, item, bottomItem, setBottomItem) => {
    console.log(item);
    event.preventDefault();
    event.currentTarget.style.background = "";
    if (item !== bottomItem) {
      bottomItem = item;
      setBottomItem(item);
      console.log("set" + item);
    }
  };

  const onDragLeaveItem = (event) => {
    event.currentTarget.style.background = "";
  };

  return (
    <Card
      onDragOver={(event) => onDragOver(event)}
      onDrop={(event) =>
        onDrop(event, listId, items, moveItem, sheetId, bottomItem)
      }
      onDragLeave={(event) => onDragLeave(event)}
      style={{ height: "30vw", width: "30vw" }}
    >
      <CardHeader
        title={
          <h4
            onDrop={(event) => onDropItem(event, 0, bottomItem, setBottomItem)}
            style={{ margin: "10px 0px" }}
          >
            {header}
          </h4>
        }
        action={
          <IconButton
            edge="end"
            aria-label="delete"
            onClick={() => deleteList({ listId, pageId: sheetId })}
          >
            <CloseIcon />
          </IconButton>
        }
      />
      <CardContent>
        <List>
          {items && items.length > 0 ? (
            items.map((item) => {
              return (
                <DraggableListItem
                  key={item._id}
                  item={item}
                  listId={listId}
                  sheetId={sheetId}
                  deleteItem={deleteItem}
                  onDragOver={(event) => onDragOverItem(event)}
                  onDrop={(event) =>
                    onDropItem(event, item._id, bottomItem, setBottomItem)
                  }
                  onDragLeave={(event) => onDragLeaveItem(event)}
                />
              );
            })
          ) : (
            <li>No items </li>
          )}
        </List>
      </CardContent>

      <CardActions
        onDrop={(event) =>
          onDropItem(event, items.length, bottomItem, setBottomItem)
        }
      >
        <AddDialog
          header="Add Item"
          fields={{ "Item Name": "name" }}
          addFunction={addItem}
          body={{ listId: listId, sheetId: sheetId }}
        />
      </CardActions>
    </Card>
  );
};

export { DraggableList };
